mapper.select:
SELECT cateno, grp, name, cnt, seqno, visible, rdate
FROM cate
WHERE cateno=#{cateno}

mapper.update:
UPDATE cate 
SET grp=#{grp}, name=#{name}, cnt=#{cnt}, seqno=#{seqno}, visible=#{visible}, rdate=SYSDATE 
WHERE cateno=#{cateno}

mapper.select:
SELECT cateno, grp, name, cnt, seqno, visible, rdate, r FROM ( SELECT cateno, grp, name, cnt, seqno, visible, rdate, rownum as r FROM ( SELECT cateno, grp, name, cnt, seqno, visible, rdate FROM cate
WHERE (UPPER(grp) LIKE '%' || UPPER(#{word}) || '%') OR (UPPER(name) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY seqno ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
