mapper.insert:
INSERT INTO delivery (
  deliveryno, memberno, rname, rtel, rzipcode, raddress1, raddress2, message, rdate
) VALUES (
  delivery_seq.NEXTVAL,
  #{memberno}, #{rname}, #{rtel}, #{rzipcode}, #{raddress1}, #{raddress2}, #{message}, SYSDATE
)

mapper.select:
SELECT deliveryno, memberno, rname, rtel, rzipcode, raddress1, raddress2, message, rdate
FROM delivery
WHERE deliveryno = #{deliveryno}

SELECT deliveryno, memberno, rname, rtel, rzipcode, raddress1, raddress2, message, rdate
FROM delivery
WHERE deliveryno = #{deliveryno}

SELECT * FROM delivery
WHERE memberno = #{memberno}
ORDER BY deliveryno DESC

mapper.update:
UPDATE delivery
SET is_default = 'N'
WHERE memberno = #{memberno}
