mapper.insert:

  SELECT orders_seq.NEXTVAL FROM dual


INSERT INTO orders (
  orderno, memberno, rname, rtel, rzipcode, 
  raddress1, raddress2, message, payment, total, point, rdate, status
) VALUES (
  #{orderno}, #{memberno}, #{rname}, #{rtel}, #{rzipcode}, 
  #{raddress1}, #{raddress2}, #{message}, #{payment}, #{total}, #{point}, SYSDATE, #{status}
)

mapper.select:
SELECT * FROM orders
WHERE memberno = #{memberno}
ORDER BY orderno DESC
