mapper.select:
SELECT COUNT(*) as cnt FROM products
WHERE cateno=#{cateno}
WHERE cateno=#{cateno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT productsno, memberno, cateno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, r, file2, file2saved, size2, file3, file3saved, size3, expdate, fileAd, fileAdsaved, sizeAd, item, maker, makedate, importyn, keep, counsel_tel, sizeinfo, origin, detail, pack, safe, dc, saleprice, point FROM ( SELECT productsno, memberno, cateno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, rownum as r, file2, file2saved, size2, file3, file3saved, size3, expdate, fileAd, fileAdsaved, sizeAd, item, maker, makedate, importyn, keep, counsel_tel, sizeinfo, origin, detail, pack, safe, dc, saleprice, point FROM ( SELECT productsno, memberno, cateno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, file2, file2saved, size2, file3, file3saved, size3, expdate, fileAd, fileAdsaved, sizeAd, item, maker, makedate, importyn, keep, counsel_tel, sizeinfo, origin, detail, pack, safe, dc, saleprice, point FROM products
WHERE cateno=#{cateno}
WHERE cateno=#{cateno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY productsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM products
WHERE productsno = #{productsno} AND passwd=#{passwd}

mapper.update:
UPDATE products
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}, 
    file2=#{file2}, file2saved=#{file2saved}, size2=#{size2}, 
    file3=#{file3}, file3saved=#{file3saved}, size3=#{size3},
    fileAd=#{fileAd}, fileAdsaved=#{fileAdsaved}, sizeAd=#{sizeAd}
WHERE productsno = #{productsno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM products 
WHERE cateno=#{cateno}

mapper.update:
UPDATE products
SET recom = recom + 1
WHERE productsno = #{productsno}

UPDATE products
SET recom = recom - 1
WHERE productsno = #{productsno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM products 
WHERE memberno=#{memberno}

mapper.update:
UPDATE products
SET replycnt = replycnt + 1
WHERE productsno = #{productsno}

UPDATE products
SET replycnt = replycnt - 1
WHERE productsno = #{productsno}

mapper.select:
SELECT *
FROM (
  SELECT productsno, title, thumb1, price, dc, saleprice, expdate,
         ROW_NUMBER() OVER (ORDER BY DBMS_RANDOM.VALUE) AS rn
  FROM products
  WHERE cateno = #{cateno}
    AND productsno != #{productsno}
    AND visible = 'Y'
)
WHERE rn BETWEEN #{start} AND #{end}
